\langle \mathbf{C}, 
    \mathbf{P}^{(t)}
    \rangle 
    - 
    \langle \mathbf{C}, 
    \mathbf{P}^{(t + 1)}
    \rangle 
    &= \frac{\rho_t}{n^{2} } 
    \sum_{i=1}^{n} 
    \sum_{j=1}^{n} 
    c(\mathbf{x}_{i}, \mathbf{y}_{j}) 
    \sum_{q=1}^{\rho_t} 
    \delta_{(\mathbf{x}_{i}, \mathbf{y}_{j}) 
    \in \Gamma_{t,q}} 
    - \frac{\rho_{t+1}}{n^{2}} 
    \sum_{i=1}^{n}
    \sum_{j=1}^{n} c(x_{i}, y_{j}) 
    \sum_{q=1}^{\rho_{t+1}} 
    \delta_{(\mathbf{x}_{i}, \mathbf{y}_{j}) \in \Gamma_{t+1,q}}\\
    &= \frac{\rho_t}{n^{2}}
    \left(
     \sum_{i=1}^{n} 
     \sum_{j=1}^{n} 
     c(\mathbf{x}_{i}, \mathbf{y}_{j}) 
     \sum_{q=1}^{ \rho_t} 
     \delta_{(\mathbf{x}_{i}, \mathbf{y}_{j}) \in \Gamma_{t,q}} - r_{t+1} 
     \sum_{i=1}^{n} 
     \sum_{j=1}^{n} 
     c(\mathbf{x}_{i}, \mathbf{y}_{j}) 
     \sum_{q'=1}^{\rho_{t\\times1}} 
     \delta_{(\mathbf{x}_{i}, \mathbf{y}_{j}) \in \Gamma_{t+1,q'}} 
     \right) \\
    &= 
    \frac{\rho_t}{n^{2}}  
    \left(
    \sum_{q=1}^{ \rho_t} 
    \sum_{i=1}^{n} 
    \sum_{j=1}^{n} 
    c(\mathbf{x}_{i}, \mathbf{y}_{j}) 
    \delta_{(\mathbf{x}_{i}, \mathbf{y}_{j}) \in \Gamma_{t,q}} 
    - r_{t+1} 
    \sum_{q'=1}^{\rho_{t+1}} 
    \sum_{i=1}^{n} 
    \sum_{j=1}^{n} c(\mathbf{x}_{i}, \mathbf{y}_{j}) 
    \delta_{(\mathbf{x}_{i}, \mathbf{y}_{j}) \in \Gamma_{t+1,q'}}
    \right)\,.

